import { createElement, selectAll, closest } from '@syncfusion/ej2-base/src/dom';import { Base, EmitType } from '@syncfusion/ej2-base/src/base';import { Browser } from '@syncfusion/ej2-base/src/browser';import { EventHandler } from '@syncfusion/ej2-base/src/event-handler';import { Property, NotifyPropertyChanges, INotifyPropertyChanged, Event } from '@syncfusion/ej2-base/src/notify-property-change';
import {Effect,AnimationOptions} from "@syncfusion/ej2-base/src/animation";

/**
 * Interface for a class Animation
 */
export interface AnimationModel {

    /**
     * Specify the type of animation
     *
     * @default : 'FadeIn';
     */
    name?: Effect;

    /**
     * Specify the duration to animate
     *
     * @default : 400;
     */
    duration?: number;

    /**
     * Specify the animation timing function
     *
     * @default : 'ease';
     */
    timingFunction?: string;

    /**
     * Specify the delay to start animation
     *
     * @default : 0;
     */
    delay?: number;

    /**
     * Triggers when animation is in-progress
     *
     * @event progress
     */
    progress?: EmitType<AnimationOptions>;

    /**
     * Triggers when the animation is started
     *
     * @event begin
     */
    begin?: EmitType<AnimationOptions>;

    /**
     * Triggers when animation is completed
     *
     * @event end
     */
    end?: EmitType<AnimationOptions>;

    /**
     * Triggers when animation is failed due to any scripts
     *
     * @event fail
     */
    fail?: EmitType<AnimationOptions>;

}